tab1, tab2, tab3 = st.tabs(['이지원의 역사','이지원의 현재', '이지원의 미래'])

with tab1:
    # tab A 를 누르면 표시될 내용
    st.write("")
    st.subheader('이지원 Lee Ji Won')
